
var express= require('express');
var socket = require('socket.io') ;

var appname = express();
var server=appname.listen(5000,function(){
console.log('your server at http:/localhost:5000')

});

appname.use(express.static('templates'));
var sio = socket(server);
sio.on('connection',function(visitor){

console.log('we have a new visitor id ='.visitor.id);
});
